#include <VlasovModDecl.h> 
void VlasovVolElc2x2vMaxP1(const double *w, const double *dxv, const double *E, const double *f, double *out) 
{ 
// w[NDIM]: Cell-center coordinates. dxv[NDIM]: Cell spacing. E/f: Input electric-field/distribution function. out: Incremented output 
  double dv10 = 2/dxv[2]; 
  double dv11 = 2/dxv[3]; 
  out[3] += 0.8660254037844386*E[2]*f[2]*dv10+0.8660254037844386*E[1]*f[1]*dv10+0.8660254037844386*E[0]*f[0]*dv10; 
  out[4] += 0.8660254037844386*E[2]*f[2]*dv11+0.8660254037844386*E[1]*f[1]*dv11+0.8660254037844386*E[0]*f[0]*dv11; 
} 
